import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-add-albums',
  templateUrl: './add-albums.component.html',
  styleUrls: ['./add-albums.component.css']
})
export class AddAlbumsComponent  {
  album!: FormGroup;
  ngOnInit(): void {
    this.album = new FormGroup({
      title: new FormControl('', [
        Validators.required,
      ]),
      creationDate: new FormControl('', [
        Validators.required,

      ]),
      archived: new FormControl('', [
        Validators.required,

      ]),
      coverPicture: new FormControl('', [Validators.required]),
      photos: new FormControl('', [Validators.required]),

    });

  }
}
<div class="container">
    <div class="row">
        <h3>Add new album</h3>
    </div>
    <div class="row">
        <form [formGroup]="album">
            <div class="mb-3">
                <label>Album Title :</label>
                <input class="form-control" formControlName="title">
                <div class="alert alert-danger" *ngIf="album.get('title')!.invalid 
                && (album.get('title')!.touched||album.get('title')!.dirty)">
                    <div *ngIf="album.get('title')!.hasError('required')">
                        album number is required</div>
                    <div *ngIf="album.get('title')!.hasError('pattern')">
                        album number invalid</div>
                </div>
            </div>
        </form>
    </div>
</div>
