import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-add-albums',
  templateUrl: './add-albums.component.html',
  styleUrls: ['./add-albums.component.css']
})
export class AddAlbumsComponent  {
updateFiles($event: any) {
  console.log($event.target.files)
}
add() {
console.log(this.album.value)

}
  album!: FormGroup;
  ngOnInit(): void {
    this.album = new FormGroup({
      title: new FormControl('', [
        Validators.required,
        Validators.pattern('[0-9 a-z A-Z]+'),
      ]),
      creationDate: new FormControl(new Date(), [
        Validators.required,
      ]),
      archived: new FormControl(0),
      coverPicture: new FormControl('', [Validators.minLength(5)]),
      photos: new FormControl([]),

    });

  }
}
<div class="container">
    <div class="row">
        <h3>Add new album</h3>
    </div>
    <div class="row">
        <form [formGroup]="album">
            <div class="mb-3">
                <label>Album Title :</label>
                <input type="text" class="form-control" formControlName="title">
                <div class="alert alert-danger" *ngIf="album.get('title')!.invalid 
                && (album.get('title')!.touched||album.get('title')!.dirty)">
                    <div *ngIf="album.get('title')!.hasError('required')">
                        Titre obligatoire</div>
                    <div *ngIf="album.get('title')!.hasError('pattern')">
                        Le 
                        titre ne doit contenir que des lettres ou des chiffres</div>
                </div>
            </div>

            <div class="mb-3">
                <label>Album coverPicture :</label>
                <input type="text" class="form-control" formControlName="coverPicture">
                <div class="alert alert-danger" *ngIf="album.get('coverPicture')!.invalid 
                && (album.get('coverPicture')!.touched||album.get('coverPicture')!.dirty)">
                    <div *ngIf="album.get('coverPicture')!.hasError('minlength')">
                        Minimum 5 caractères</div>
                </div>
            </div>

            <div class="mb-3">
                <button  [disabled]="album.invalid" type="sumbit" value="Submit" class="btn btn-outline-success" (click)="add()">Submit</button>
            </div>
        </form>
    </div>
</div>
